t doc = new DefaultStyledDocument();		StyleContext sc = new StyleContext();		Style def = sc.getStyle(StyleContext.DEFAULT_STYLE);		Style normal = sc.addStyle("Normal", def);		// StyleConstants.setFontFamily(normal,"TimesRoman");		StyleConstants.setFontSize(normal, 8);		for(int i = 0; i < lines.length; i++)		{			try			{				doc.insertString(doc.getEndPosition().getOffset() - 1, lines[i]						+ lineSeparator, normal);			}			catch(BadLocationException ble)			{				System.err.println("Could not insert string for line: "						+ lines[i]);			}		}		addDocument(doc);	}	/**	 * adds the given document to this frame	 */	void addDocument(StyledDocument doc)	{		_doc = doc;		JTextPane jtp = new ReportPane();		jtp.setEditable(false);		jtp.setDocument(doc);		setLayout(new BorderLayout());		add(new JScrollPane(jtp), BorderLayout.CENTER);	}	/**	 *	 */	public String getFrameTitle()	{		return _frameTitle;	}	/**	 *	 */	public void setFrameTitle(String str)	{		_frameTitle = str;	}	/**	 *	 */	public JMenuBar getJMenuBar()	{		if(_mbar == null)		{			_mbar = createJMenuBar();		}		return _mbar;	}	/**	 *	 */	public JMenuBar createJMenuBar()	{		int mindex = 0;		JMenuItem saveItem = new JMenuItem(itemText[mindex]);		saveItem.setToolTipText(toolTipText[mindex]);		saveItem.setAccelerator(KeyStroke.getKeyStroke(itemKeys[mindex],				KeyEvent.CTRL_MASK));		saveItem.addActionListener(new ActionListener()		{			public void actionPerformed(ActionEvent evt)			{				save();			}		});		//		mindex++;		JMenuItem saveAsHtmlItem = new JMenuItem(itemText[mindex]);		// saveAsHtmlItem.setEnabled(false); // ?? temporary		saveAsHtmlItem.setToolTipText(toolTipText[mindex]);		saveAsHtmlItem.setAccelerator(KeyStroke.getKeyStroke(itemKeys[mindex],				KeyEvent.CTRL_MASK));		saveAsHtmlItem.addActionListener(new ActionListener()		{			public void actionPerformed(ActionEvent evt)			{				saveAsHtml();			}		});		mindex++;		//		JMenuItem printItem = new JMenuItem(itemText[mindex]);		printItem.setToolTipText(toolTipText[mindex]);		printItem.setAccelerator(KeyStroke.getKeyStroke(itemKeys[mindex],				KeyEvent.CTRL_MASK));		printItem.addActionListener(new ActionListener()		{			public void actionPerformed(ActionEvent evt)			{				print();			}		});		//		mindex++;		JMenuItem quitItem = new JMenuItem(itemText[mindex]);		quitItem.setToolTipText(toolTipText[mindex]);		quitItem.setAccelerator(KeyStroke.getKeyStroke(itemKeys[mindex],				KeyEvent.CTRL_MASK));		quitItem.addActionListener(new ActionListener()		{			public void actionPerformed(ActionEvent evt)			{				quit();			}		});		//		//		JMenu fileMenu = new JMenu("File");		fileMenu.add(saveItem);		fileMenu.add(saveAsHtmlItem);		fileMenu.add(printItem);		fileMenu.addSeparator();		fileMenu.addSeparator();		fileMenu.add(quitItem);		//		_mbar = new JMenuBar();		_mbar.add(fileMenu);		return _mbar;	}	/**	 *	 */	void print()	{		if(DEBUG)		{			System.out.println("Print");		}		Style s = null;		try		{			s = _doc.getStyle("main");			StyleConstants.setFontSize(s, 7);			Style dateStyle = _doc.getStyle("date style");			if(dateStyle != null)			{				StyleConstants.setFontSize(dateStyle, 5);			}			GuiUtils.print(GuiUtils.getComponent(JTextPane.class, this));		}		catch(Exception e)		{			VistaUtils.displayException(this, e);		}		finally		{			if(s != null)			{				s = _doc.getStyle("main");				StyleConstants.setFontSize(s, 12);				Style dateStyle = _doc.getStyle("date style");				if(dateStyle != null)				{					StyleConstants.setFontSize(dateStyle, 5);				}			}		}	}	/**	 *	 */	void save()	{		if(_doc != null)		{			try			{				String saveFile = VistaUtils.getFilenameFromDialog(this,						FileDialog.SAVE,						"txt", "Text File");				if(saveFile == null)				{					return;				}				PrintWriter writer = new PrintWriter(new FileWriter(saveFile));				String txt = _doc.getText(0, _doc.getLength());				java.util.StringTokenizer st =						new java.util.StringTokenizer(txt, System								.getProperty("line.separator"));				while(st.hasMoreTokens())				{					String line = st.nextToken().trim();					writer.println(line);				}				writer.close();			}			catch(Exception e)			{				VistaUtils.displayException(this, e);			}		}		if(DEBUG)		{			System.out.println("Save");		}	}	/**	 *	 */	void saveAsHtml()	{		if(_doc != null)		{			try			{				String saveFile = VistaUtils.getFilenameFromDialog(this,						FileDialog.SAVE,						"html", "HTML files");				if(saveFile == null)				{					return;				}				FileWriter writer = new FileWriter(saveFile);				new MinimalHTMLWriter(writer, _doc).write();				writer.close();			}			catch(Exception e)			{				VistaUtils.displayException(this, e);			}		}		if(DEBUG)		{			System.out.println("Save As Html");		}	}	/**	 *	 */	void quit()	{		if(DEBUG)		{			System.out.println("Quit");		}		JOptionPane.getFrameForComponent(this).dispose();	}}